import React, { Component } from 'react'

export class NewsItem extends Component {
    
  render() {
      let {description,title,imgurl}=this.props;
    return (
      <div>
        <div className="card my-3" style={{width: "18rem",textAlign:"center"}}>
            <img src={imgurl} className="card-img-top" alt="..." style={{objectFit:"contain",maxHeight:"160px"}}/>
            <div className="card-body">
                <h5 className="card-title" style={{height:"72px"}}>{title.slice(0,71)}+""...</h5>
                <p className="card-text">{description}</p>
                <a href="/" className="btn btn-primary">Read more</a>
            </div>
        </div>
      </div>
    )
  }
}

export default NewsItem
