import React, { Component } from 'react'

export class NewsItem extends Component {
    
  render() {
      let {description,title,imgurl,newsurl}=this.props;
    return (
      <div>
        <div className="card my-3" style={{/*width: "18rem",*/textAlign:"center",height:"500px"}}>
            <img src={imgurl} className="card-img-top" alt="..." style={{objectFit:"contain",maxHeight:"50%",width:"100%"}}/>
            <div className="card-body">
                <h5 className="card-title" style={{maxHeight:"40%"}}>{title}</h5>
                <p className="card-text">{description}...</p>
            </div>
            <div class="d-flex justify-content-center" style={{height:"6%",margin:"2rem"}}>
                <a href={newsurl} target="_blank" className="btn btn-secondary btn-sm" style={{/*position:"relative",top:"5%"*/}}>Read more</a>
            </div>
        </div>
      </div>
    )
  }
}

export default NewsItem
